changeSet.sql:
CREATE TABLE UsersH ( id INT NOT NULL, screenadaptationname TEXT DEFAULT NULL, year TEXT DEFAULT NULL, mark INT DEFAULT NULL, PRIMARY KEY(id) );
CREATE TABLE ChatsH ( id INT NOT NULL, screenadaptationname TEXT DEFAULT NULL, dateOfCreating DATE DEFAULT NULL, PRIMARY KEY(id) );
CREATE TABLE UsersChatsH ( id INT NOT NULL, userId INT NOT NULL REFERENCES UsersH.id, editionId INT NOT NULL REFERENCES ChatsH.id, PRIMARY KEY(id) );
CREATE TABLE NotificationsH ( id INT NOT NULL, userId INT NOT NULL REFERENCES UsersH.id, high TEXT DEFAULT NULL, PRIMARY KEY(id) );
CREATE TABLE UserMessagesH ( id INT NOT NULL, userId INT NOT NULL REFERENCES UsersH.id, editionId INT NOT NULL REFERENCES ChatsH.id, departureDate TIMESTAMP NOT NULL, high TEXT DEFAULT NULL, PRIMARY KEY(id) );
CREATE TABLE NewsH ( id INT NOT NULL, editionId INT NOT NULL REFERENCES ChatsH.id, dateOfCreating TIMESTAMP NOT NULL, high TEXT DEFAULT NULL, PRIMARY KEY(id) );
